import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component{
  constructor(){
    super();

    this.state = {
      name: {firstName: 'Frank', lastName: 'Omondi'}, 
      company: 'ZTM'
    }
  }

  render() {

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi there, I am {this.state.name.firstName} {this.state.name.lastName}, I go to study at {this.state.company}
          </p>
          <button 
          onClick={() =>{
            this.setState();
            console.log(this.state)
          }}
          >

            Use a different name
          </button>
        </header>
      </div>
    );
    }
  }


export default App;
